mapper.update:
update app_market_resource
set
    download_num = download_num + 1,
    update_time = now()
where
    market_id = #{marketId}
and app_id = #{appId}
and tenant_id = #{tenantId}
and deleted = 0

mapper.select:
select amr.* from app_market_resource amr inner join ( select app_id,market_id,category,max(app_version) from app_market_version where deleted = 0 group by app_id,market_id,category ) as al on al.app_id = amr.app_id and al.market_id = amr.market_id
amr.deleted = 0
and al.category = #{category}
and amr.market_id = #{marketId}
and amr.creator_id = #{creatorId}
and amr.app_name like concat('%', #{appName}, '%')
order by amr.download_num desc
order by amr.check_num desc
order by amr.create_time desc

SELECT amr.app_id, amr.market_id, amr.app_name, u.name as creator_name, u.phone as creator_phone, amr.update_time, CASE WHEN amr.deleted = 0 THEN '已上架' WHEN amr.deleted = 1 THEN '已下架' ELSE '未知状态' END as status, amr.robot_id, amv.version FROM app_market_resource amr LEFT JOIN ${databaseName}.t_uap_user u ON amr.creator_id = u.id LEFT JOIN ( SELECT amv1.app_id, amv1.market_id, amv1.app_version AS version, amv1.category FROM app_market_version amv1 LEFT JOIN app_market_version amv2 ON ( amv1.app_id = amv2.app_id AND amv1.market_id = amv2.market_id AND amv1.app_version < amv2.app_version ) WHERE amv2.app_version IS NULL ) amv ON amr.app_id = amv.app_id AND amr.market_id = amv.market_id
amr.market_id = #{request.marketId} and amr.tenant_id = #{tenantId}
AND amr.app_name LIKE CONCAT('%', #{request.appName}, '%')
AND u.id = #{request.creatorId}
AND amv.category = #{request.categoryId}
AND amr.deleted = #{request.status}
AND DATE_FORMAT(amr.update_time, '%Y-%m-%d') >= #{request.updateTimeStart}
AND DATE_FORMAT(amr.update_time, '%Y-%m-%d') <= #{request.updateTimeEnd}
ORDER BY amr.update_time DESC

SELECT id
FROM app_market_resource
WHERE app_id = #{appId}
AND market_id = #{marketId}
AND tenant_id = #{tenantId}
AND deleted = 1
