mapper.select:
select
    *
from
    robot_version rv
inner join app_market_resource amr on amr.robot_id = rv.robot_id
where
    amr.market_id = #{marketId}
    and amr.app_id = #{appId}
    and amr.deleted = 0
    and rv.version = #{version}

select *
from
    robot_version
where
    robot_id = #{robotId}
and creator_id = #{userId}
and tenant_id = #{tenantId}
and deleted = 0
order by version desc
limit 1

select file_id, file_name from file where file_id in
#{fileId}
and deleted = 0

select version
from robot_version
where
    robot_id = #{robotId}
  and creator_id = #{creatorId}
  and tenant_id = #{tenantId}
  and deleted = 0
order by version desc
limit 1

mapper.update:
update
robot_version
set
    online = 0
where
    robot_id = #{robotId}
and
    creator_id = #{userId}
and tenant_id = #{tenantId}
