mapper.select:
SELECT user_id
FROM ${databaseName}.t_uap_user_role
WHERE role_id = #{roleId}
AND tenant_id = #{tenantId}

SELECT id
FROM ${databaseName}.t_uap_user_role
WHERE role_id = '1'
AND tenant_id = #{tenantId}
AND user_id IN

    #{userId}

mapper.update:
UPDATE ${databaseName}.t_uap_user_role 
SET role_id = '1'
WHERE user_id IN

    #{userId}

AND tenant_id = #{tenantId}

mapper.select:
SELECT COUNT(*)
FROM ${databaseName}.t_uap_tenant_role
WHERE tenant_id = #{tenantId}
AND role_id = #{roleId}

mapper.update:
